mapper.select:
SELECT COUNT(*) > 0
FROM MATCH_PREDICT
WHERE MEMBER_ID = #{memberId}
AND GAME_NO = #{gameNo}
AND MATCH_PREDICT_DECISION = (
SELECT GAME_RESULT
FROM GAME
WHERE GAME_NO = #{gameNo}
)

mapper.update:
UPDATE MATCH_PREDICT
SET MATCH_PREDICT_DECISION = #{matchPredictDecision}
WHERE GAME_NO = #{gameNo} AND MEMBER_ID = #{memberId}
